mapper.select:
SELECT ORGAN_CODE, VISIT_CARD_NO AS INDEX_NO, '2' AS INDEX_NO_TYPE, PAT_INDEX_NO FROM v_jh_cardnoinfo WHERE VISIT_CARD_TYPE = '2' AND PAT_NAME=#{patName} AND VISIT_CARD_NO=#{visitCardNo}
AND OPEN_DATE >= to_date(#{startDate},'yyyy-mm-dd hh24:mi:ss')
AND OPEN_DATE <= to_date(#{endDate},'yyyy-mm-dd hh24:mi:ss')
UNION SELECT ORGAN_CODE, VISIT_CARD_NO AS INDEX_NO, '1' AS INDEX_NO_TYPE, PAT_INDEX_NO FROM v_jh_cardnoinfo WHERE VISIT_CARD_TYPE = '1' AND PAT_NAME=#{patName} AND VISIT_CARD_NO=#{visitCardNo}
AND OPEN_DATE >= to_date(#{startDate},'yyyy-mm-dd hh24:mi:ss')
AND OPEN_DATE <= to_date(#{endDate},'yyyy-mm-dd hh24:mi:ss')

SELECT ORGAN_CODE, VISIT_CARD_NO AS INDEX_NO, #{indexNoType} AS INDEX_NO_TYPE, PAT_INDEX_NO FROM v_jh_cardnoinfo WHERE VISIT_CARD_NO=#{visitCardNo} AND VISIT_CARD_TYPE = #{visitCardType}
AND OPEN_DATE >= to_date(#{startDate},'yyyy-mm-dd hh24:mi:ss')
AND OPEN_DATE <= to_date(#{endDate},'yyyy-mm-dd hh24:mi:ss')
